	int len = recv(s, buffer, 32, 0);

	if (strcmp(buffer, "ACCEPT\\1") == 0) {
		send(s, data, dataLength, 0);
	}
	else {
		closesocket(s);
		return false;
	}
	len = recv(s, buffer, 32, 0);

	free(buffer);
	closesocket(s);

	return true;
}
