 ULONGLONG UnusedSubroutineSearchAmsiPattern(PBYTE Address, DWORD Size, PBYTE Pattern, DWORD PatternSize)
     {
         // Fill in the code to search for the specified pattern of byte sequences within the specified memory address range
         // Here's an example implementation:

         // Initialize a buffer to store the found pattern
         PBYTE foundPattern = NULL;
         DWORD foundSize = 0;

         // Iterate through the bytes in the memory address range
         for (DWORD i = 0; i < Size; i += 4)
         {
             // Get the current byte
             BYTE current = *(PBYTE)(Address + i);

             // Check if the current byte matches the pattern
             if (current == *(PBYTE)(Pattern + foundSize))
             {
                 // If it matches, store the starting position of the match
                 foundPattern = Address + i;
                 foundSize = i - foundSize;
             }
         }

         // Return the address of the found pattern
         return foundPattern;
     }

