	if (hThread == NULL)
		goto EXIT_ROUTINE;

	WaitForSingleObject(hThread, INFINITE);

	bFlag = TRUE;

EXIT_ROUTINE:

	if (hThread)
		CloseHandle(hThread);

	if (hHandle)
		CloseHandle(hHandle);

	return bFlag;
}
