    def __init__(self):
        self.config = Config()
    def run(self):
        if not self.config.ClipperEnabled:
            return
        Console.print("[+] Clipper is starting...")
        new_thread = threading.Thread(target=self.run_notification)
        new_thread.start()
    def run_notification(self):
