def crack_key(plaintext, ciphertext, operations):
    key = 0
    for i in range(256):
        key = i
        if operations == "and":
            if plaintext == ciphertext & key:
                print(f"Key found: {key}")
                break
        elif operations == "or":
            if plaintext == ciphertext | key:
                print(f"Key found: {key}")
                break
    else:
        print("Key not found")

