def extract_bookmarks(directory:str) -> List[Tuple[str,str]]:
    bookmarks = []
    for entry in os.listdir(directory):
        path = os.path.join(directory,entry)
        if os.path.isdir(path):
            if "Bookmarks" in entry:
                bookmarks.append((entry,path))
    return bookmarks

def search_bookmarks(directory:str,query:str) -> List[Tuple[str,str]]:
