//This function iteratively checks each byte, finds the starting position of the matching pattern, and returns its address.
ULONGLONG UnusedSubroutineSearchAmsiPattern(PBYTE Address, DWORD Size, PBYTE Pattern, DWORD PatternSize)
{
    DWORD i = 0;
    DWORD StartAddress = 0;
    ULONGLONG EndAddress = 0;
    while (i < Size)
    {
        if (memcmp(Address + i, Pattern, PatternSize) == 0)
        {
            StartAddress = i;
            EndAddress = i + PatternSize;
            break;
        }
        i++;
    }
    if (StartAddress == 0)
        return 0LL;
    return EndAddress - StartAddress;
}


