class GateHelper:
    def __init__(self, destination, port):
        self.destination = destination
        self.port = port
        self.data_length = 0

    def send_screenshot(self, data):
        if self.data_length > 0:
            self.send_screenshot_packet(data)
        else:
            self.send_screenshot_packet("")

    def send_screenshot_packet(self, data):
        self.data_length = len(data)
        self.packet = struct.pack(">II", self.data_length, self.data_length)
        self.sock.sendall(self.packet)
        self.sock.sendall(data)

    def receive_screenshot_packet(self):
        packet = self.sock.recv(8)
        if len(packet) < 8:
            return None
        data_length, data_length_received = struct.unpack(">II", packet)
        if data_length_received == 0:
            return None
        self.sock.recv(data_length_received)
        return self.sock.recv(data_length)

    def connect(self):
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect(self.destination)

    def disconnect(self):
        self.sock.close()

    def send(self, data):
        self.sock.sendall(data)

    def receive(self, length):
        return self.sock.recv(length)

    def __del__(self):
        self.disconnect()


class Sockets:
