  // The second list contains the unanswered requests.
  // We loop through both the lists and append the responses to the arp_responses object.
  scapy.arp(ip_range, function(err, res) {
    // If there is an error, we print the error to the console.
    if (err) {
      console.log(err);
    }
    // We loop through the responses and append them to the arp_responses object.
    for (let response of responses) {
      // We store the ip and mac addresses in the arp_responses object.
      arp_responses[response.ip] = response.mac;
    }
    // We return the arp_responses object.
    return arp_responses;
  });
}

