import ctypes
import image
import png
import sys

def screenRect():
    hDC = w32.GetDC(0)
    if hDC == 0:
        return (image.Rectangle, "Could not Get primary display err:%d\n" % w32.GetLastError())
    defer w32.ReleaseDC(0, hDC)
    x = w32.GetDeviceCaps(hDC, w32.HORZRES)
    y = w32.GetDeviceCaps(hDC, w32.VERTRES)
    return (image.Rect(0, 0, x, y), None)

def captureScreen(compressImage=False):
    rect, err = screenRect()
    if err!= None:
        return (None, err)
    return captureRect(compressImage, rect)

def captureRect(compressImage=False, rect: image.Rectangle):
    hDC = w32.GetDC(0)
    if hDC == 0:
        return (None, "Could not Get primary display err:%d.\n" % w32.GetLastError())
    defer w32.ReleaseDC(0, hDC)

    m_hDC = w32.CreateCompatibleDC(hDC)
    if m_hDC == 0:
        return (None, "Could not Create Compatible DC err:%d.\n" % w32.GetLastError())
    defer w32.DeleteDC(m_hDC)

    x, y = rect.Dx(), rect.Dy()

    bt = w32.BITMAPINFO()
    bt.BmiHeader.BiSize = (ctypes.c_ulong(len(bt.BmiHeader))).value
    bt.BmiHeader.BiWidth = int32(x)
    bt.BmiHeader.BiHeight = -int32(y)
    bt.BmiHeader.BiPlanes = 1
    bt.BmiHeader.BiBitCount = 32
    bt.BmiHeader.BiCompression = w32.BI_RGB

    ptr = ctypes.c_ulong(0)

    m_hBmp = w32.CreateDIBSection(m_hDC, ctypes.byref(bt), w32.DIB_RGB_COLORS, ctypes.byref(ptr), 0, 0)
    if m_hBmp == 0:
        return (None, "Could not Create DIB Section err:%d.\n" % w32.GetLastError())
    if m_hBmp == w32.InvalidParameter:
        return (None, "One or more of the input parameters is invalid while calling CreateDIBSection.\n")
    defer w32.DeleteObject(w32.HGDIOBJ(m_hBmp))

    obj = w32.SelectObject(m_hDC, w32.HGDIOBJ(m_hBmp))
    if obj == 0:
        return (None, "error occurred and the selected object is not a region err:%d.\n" % w32.GetLastError())
    if obj == 0xffffffff:  # GDI_ERROR
        return (None, "GDI_ERROR while calling SelectObject err:%d.\n" % w32.GetLastError())
    defer w32.DeleteObject(obj)

    #Note:BitBlt contains bad error handling, we will just assume it works and if it doesn't it will panic :x
    w32.BitBlt(m_hDC, 0, 0, x, y, hDC, rect.Min.X, rect.Min.Y, w32.SRCCOPY)

    def _get_bitmap_info(hObject):
        if hObject == 0:
            return None
        obj = w32.GetObject(hObject, w32.OBJ_BITMAPINFO)
        return w32.GetBitmapInfo(obj)

    def _get_bitmap_info_ptr(hObject):
        if hObject == 0:
            return None
        obj = w32.GetObject(hObject, w32.OBJ_BITMAPINFO)
        return ctypes.cast(w32.GetBitmapInfo(obj), ctypes.POINTER(ctypes.c_ulong))

    bm = _get_bitmap_info(m_hBmp)
    if bm is None:
        return (None, "Could not get bitmap info\n")
    rowBytes = (x * 4 + 3) & ~3
    bmp = png.Png(rowBytes, bm.BmiHeader.BiWidth, bm.BmiHeader.BiHeight)
    if compressImage:
        bmp.save(sys.stdout, "PNG")
    else:
        bmp.save(sys.stdout, "PNG")
    return bmp.data
