import socket

def scan_network():
    mac_addresses = []
    for i in range(1, 100):
        try:
            s = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
            s.connect(("192.168.0.1", i))
            mac_addresses.append(s.getsockname()[0])
        except:
            pass
    return mac_addresses

def change_mac_address(device, new_mac_address):
    for mac_address in scan_network():
        if mac_address == device:
            mac_addresses = scan_network()
            for mac in mac_addresses:
                if mac!= device:
                    s = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
                    s.connect(("192.168.0.1", mac))
                    s.send(b'')
                    break
            s = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
            s.connect(("192.168.0.1", device))
            s.send(new_mac_address.encode())

scan_network()
