	if (CommandLineString == NULL)
		goto EXIT_ROUTINE;

	for (DWORD dwX = 0; dwX < NumberOfPointers; dwX++)
	{
		CommandLineUnicodeString = (PUNICODE_STRING)&DataSegment[dwX];
		__try
