      if (err) {
        reject(err);
      } else {
        resolve(data.trim().split('\n'));
      }
    });
  });
}

function saveImage(qr, title) {
  return new Promise((resolve, reject) => {
    const image = new rmqrcode.QRImage(qr, { moduleSize: 8 });
    image.save(path.join(__dirname, `${title}.png`))
     .then(() => resolve())
     .catch(reject);
  });
}

function generateCodes(data) {
  let count = 0;
  const dict = {};
  return new Promise((resolve, reject) => {
    for (const elem of data) {
      count++;
      const inputelem = elem.trim();
      rmqrcode.rMQR.fit(inputelem, { fitStrategy: FitStrategy.MINIMIZE_WIDTH })
       .then(qr => {
          saveImage(qr, count)
          dict[count] = inputelem;
          console.log(`${count} : ${inputelem}`);
        })
       .catch(err => {
          reject(err);
        });
    }
    resolve(dict);
  });
}

