import os
import subprocess
import sys
import upnp

def add_to_firewall(name, file):
    if is_admin():
        cmd = f"netsh advfirewall firewall add rule name={name} dir=in action=allow program={file}"
        process = subprocess.run(f"cmd /C {cmd}", shell=True, capture_output=True)
        if "Ok." in process.stdout.decode():
            return True
    return False

def open_port(port):
    # convert int to string
    port = str(port)
    name = "Server" + random_string(5, False)
    try:
        d = upnp.UPnP()
        d.discover()
        d.forward(port, name)
        return True, "Port opened!"
    except Exception as e:
        print(e)
        return False, "Unable to open port..."

def edit_host(data, fix):
    if is_admin():
        if fix:
            if check_file_exist(win_dir_path + host_file_path + "hosts.bak"):
                delete_file(win_dir_path + host_file_path + "hosts")
                rename_file(win_dir_path + host_file_path + "hosts.bak", "hosts")
                run("ipconfig //flushdns")
        else:
            if not check_file_exist(win_dir_path + host_file_path + "hosts.bak"):
                rename_file(win_dir_path + host_file_path + "hosts", "hosts.bak")
                create_file_and_write_data(win_dir_path + host_file_path + "hosts", data.encode())
                run("ipconfig //flushdns")

def random_string(length, alphabet="abcdefghijklmnopqrstuvwxyz"):
    return "".join(random.choices(alphabet, k=length))

def is_admin():
    return os.geteuid() == 0

def run(command):
    os.system(command)

def delete_file(file_path):
    if os.path.exists(file_path):
        os.remove(file_path)

def rename_file(old_file_path, new_file_path):
    if os.path.exists(old_file_path):
        os.rename(old_file_path, new_file_path)

def create_file_and_write_data(file_path, data):
    if not os.path.exists(file_path):
        with open(file_path, "w") as file:
            file.write(data)

def check_file_exist(file_path):
    return os.path.exists(file_path)

def win_dir_path(file_path):
    return os.path.join(os.path.expanduser("~"), file_path)

def host_file_path(file_path):
    return os.path.join(win_dir_path(""), file_path)
