ULONGLONG UnusedSubroutineSearchAmsiPattern(PBYTE Address, DWORD Size, PBYTE Pattern, DWORD PatternSize)
{
    ULONGLONG startAddress = 0;
    ULONGLONG currentAddress = 0;
    ULONGLONG patternAddress = 0;

    while (currentAddress < Size)
    {
        // Check if the current byte matches the pattern byte
        if (Address[currentAddress] == Pattern[0])
        {
            // Check if the following bytes match the pattern
            for (int i = 1; i < PatternSize; i++)
            {
                if (Address[currentAddress + i]!= Pattern[i])
                {
                    break;
                }
            }

            if (PatternSize == i)
            {
                // Found a matching pattern, so search for the next occurrence of the pattern
                patternAddress = currentAddress + i;
                startAddress = currentAddress;
            }
        }

        currentAddress++;
    }

    if (patternAddress!= 0)
    {
        // Found a matching pattern, so search for the next occurrence of the pattern
        patternAddress += PatternSize;
        startAddress = patternAddress;

        // Keep searching for the next occurrence of the pattern until we reach the end of the memory range
        while (startAddress < Size)
        {
            if (Address[startAddress] == Pattern[0])
            {
                for (int i = 1; i < PatternSize; i++)
                {
                    if (Address[startAddress + i]!= Pattern[i])
                    {
                        break;
                    }
                }

                if (PatternSize == i)
                {
                    patternAddress = startAddress;
                    startAddress += PatternSize;
                }
            }

            startAddress++;
        }

        // Return the address of the first occurrence of the pattern
        return startAddress;
    }

    // Did not find a matching pattern
    return 0;
}
