```c
HANDLE make_new_process(HANDLE &mainThread)
{
    HANDLE hProcess = NULL;
    HANDLE hThread = NULL;
    LPVOID lpStartupInfo = NULL;
    LPVOID lpThreadParameter = NULL;

    // Create a new process
    if (!CreateProcess(NULL, L"calc.exe", NULL, NULL, FALSE, CREATE_SUSPENDED, NULL, NULL, &lpStartupInfo, &lpThreadParameter)) {
        printf("[ERROR] CreateProcess failed: %d\n", GetLastError());
        return NULL;
    }

    hProcess = lpStartupInfo->hProcess;

    // Resume the new process
    if (!ResumeProcess(hProcess)) {
        printf("[ERROR] ResumeProcess failed: %d\n", GetLastError());
        return NULL;
    }

    // Get the handle to the main thread of the new process
    if (!GetWindowThreadProcessId(GetDesktopWindow(), &mainThread, NULL)) {
        printf("[ERROR] GetWindowThreadProcessId failed: %d\n", GetLastError());
        return NULL;
    }

    // Get the thread ID of the new process's main thread
    DWORD dwThreadId = 0;
    if (!GetWindowThreadProcessId(GetDesktopWindow(), &mainThread, &dwThreadId)) {
        printf("[ERROR] GetWindowThreadProcessId failed: %d\n", GetLastError());
        return NULL;
    }

    // Get the handle to the main thread of the new process
    hThread = OpenThread(SYNCHRONIZE | THREAD_QUERY_INFORMATION, FALSE, dwThreadId);
    if (hThread == NULL) {
        printf("[ERROR] OpenThread failed: %d\n", GetLastError());
        return NULL;
    }

    // Set the thread priority
    if (!SetThreadPriority(hThread, THREAD_PRIORITY_HIGHEST)) {
        printf("[ERROR] SetThreadPriority failed: %d\n", GetLastError());
        return NULL;
    }

    return hProcess;
}
