	int cRes = connect(s, (sockaddr*)&input, sizeof(input));
	if (cRes == SOCKET_ERROR) {
		closesocket(s); return false;
	}

	send(s, botId, strlen(botId), 0);

	char* buffer = (char*)malloc(32);
	ZeroMemory(buffer, 32);
	int len = recv(s, buffer, 32, 0);

