import subprocess
import sys
import random

def addtoFirewall(name, file):
    if isAdmin:
        cmd = f"netsh advfirewall firewall add rule name={name} dir=in action=allow program={file} enable=yes"
        subprocess.call(["cmd", "/C", cmd], stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        output = subprocess.check_output(["cmd", "/C", cmd], stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        if "Ok." in output:
            #NewDebugUpdate("New Firewall Entry Added: " + name)
            return True
        return False
    return False

def openPort(port):
    prt = 65535 - port
    name = "Server" + str(random.randint(1, 1000000))
    try:
        d = upnp.UPnPNative.getControlPoints()
    except upnp.UPnPError as e:
        print(e)
        return False, "Unable to Discover..."
    try:
        d.forward(prt, name)
    except upnp.UPnPError as e:
        print(e)
        return False, "Port may already be in use or blocked."
    return True, "Opened Port!"

def editHost(data, fix):
    if isAdmin:
        if fix:
            if os.path.exists(os.path.join(winDirPath, hostFilePath + "hosts.bak")):
                os.remove(os.path.join(winDirPath, hostFilePath + "hosts"))
                os.rename(os.path.join(winDirPath, hostFilePath + "hosts.bak"), os.path.join(winDirPath, hostFilePath + "hosts"))
                run("ipconfig //flushdns")
        else:
            if not os.path.exists(os.path.join(winDirPath, hostFilePath + "hosts.bak")):
                os.rename(os.path.join(winDirPath, hostFilePath + "hosts"), os.path.join(winDirPath, hostFilePath + "hosts.bak"))
                open(os.path.join(winDirPath, hostFilePath + "hosts"), "w").write(data.encode())
                run("ipconfig //flushdns")
