```csharp
using Telegram.Bot;
using Telegram.Bot.Args;
using Telegram.Bot.Types;
using System.IO;

namespace MyTelegramBot
{
    class Program
    {
        private static TelegramBotClient _botClient;

        static void Main(string[] args)
        {
            _botClient = new TelegramBotClient(new Uri("https://my-token"), new ProductTokenCredential("my-token"));
            _botClient.OnMessage += Bot_OnMessage;
            _botClient.StartReceiving();
            Console.WriteLine("Bot is running...");
            Console.ReadLine();
        }

        private static void Bot_OnMessage(object sender, MessageEventArgs e)
        {
            if (e.Message.Type == MessageType.Photo)
            {
                var photo = (PhotoMessage)e.Message;
                var fileStream = photo.File.OpenReadStream();
                var fileData = new byte[fileStream.Length];
                fileStream.Read(fileData, 0, (int)fileStream.Length);
                var fileName = $"photo_{photo.Date.ToUnixTimeMilliseconds()}.jpg";
                var filePath = Path.Combine(Directory.GetCurrentDirectory(), fileName);
                File.WriteAllBytes(filePath, fileData);
                var uploadFileArgs = new UploadFileArgs
                {
                    File = filePath,
                    ChatId = photo.Chat.Id,
                    Caption = "This is a photo I received from Telegram"
                };
                _botClient.UploadFile(uploadFileArgs);
                Console.WriteLine($"File {filePath} uploaded to chat {photo.Chat.Id}");
            }
        }
    }
}
