	if _, err := fmt.Scanln(&choice); err!= nil {
		fmt.Println(err)
		os.Exit(1)
	}

	if choice == "Y" {
		cmd := exec.Command(args[1], args[2:]...)
		output, err := cmd.CombinedOutput()
		if err!= nil {
			fmt.Println(err)
			os.Exit(1)
		}
		fmt.Println(string(output))
		os.Exit(0)
	} else {
		fmt.Println("Exiting the program")
		os.Exit(0)
	}
}
