                    string decryptedPassword = Encoding.Default.GetString(Sodium.SecretAeadAes.Decrypt(payload, iv, masterKeyBytes));
                    return decryptedPassword;
                }
                catch { return "failed (AES-GCM)"; }

                // return decryptedPassword;
