namespace _02.LinkedLists
{
    public class Program
    {
        public static void Main()
        {
            LinkedList<int> list1 = new LinkedList<int>();
            LinkedList<int> list2 = new LinkedList<int>();

            list1.First = 1;
            list1.Next = 2;
            list1.Previous = 1;

            list2.First = 3;
            list2.Next = 4;
            list2.Previous = 3;

            MergeTwoLists(list1, list2);

            Console.WriteLine("The merged linked list: ");
            PrintList(list1);

            Console.ReadKey();
        }

        private static void MergeTwoLists(LinkedList<int> list1, LinkedList<int> list2)
        {
            LinkedListNode<int> curr = list1.First;
            LinkedListNode<int> next = curr.Next;

            while (curr!= null && next!= null)
            {
                if (next.Data < curr.Data)
                {
                    curr.Next = next;
                    next.Previous = curr;
                }
                else
                {
                    curr.Next = next;
                    next.Previous = curr;
                }

                curr = curr.Next;
                next = next.Next;
            }

            while (curr!= null)
            {
                curr.Next = list2.First;
                curr = curr.Next;
            }
        }

        private static void PrintList(LinkedList<int> list)
        {
            Console.WriteLine("{0}", list.First.Data);

            LinkedListNode<int> current = list.First;

            while (current!= null)
            {
                Console.WriteLine("{0}, {1}", current.Data, current.Next);
                current = current.Next;
            }
        }
    }
}
